import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { useLocation } from "react-router-dom";
import Inicio from "./components/Inicio";
import Añadir from "./components/Añadir";
import Perfil from "./components/Perfil";
import Tutoriales from "./components/Tutoriales";
import Menu from "./components/Menu";

import './App.css'; // Asegúrate de que el CSS para las animaciones esté importado

function App() {
  const [personas, setPersonas] = useState([]);
  const location = useLocation(); // Obtener la ubicación actual para las animaciones

  return (
    <Router>
      <Menu />
      <div className="container mt-3">
        <div className={`page-container fade-in`}>
          <Switch location={location}>
            <Route exact path="/" component={Inicio} />
            <Route path="/añadir" component={Añadir} />
            <Route path="/perfil" component={Perfil} />
            <Route path="/tutoriales" component={Tutoriales} />
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
