import React, { useState, useEffect } from "react";
import AgendaDataService from "../services/agenda.service";
import { Modal, Button, Container, Row, Col, Form, Table } from "react-bootstrap";
import { motion } from "framer-motion";

const Inicio = () => {
  const [personas, setPersonas] = useState([]);
  const [searchPersona, setSearchPersona] = useState("");
  const [selectedPersona, setSelectedPersona] = useState(null);
  const [modalState, setModalState] = useState("informacion");
  const [tutorials, setTutorials] = useState([]);
  const [selectedTutorial, setSelectedTutorial] = useState(null);

  useEffect(() => {
    retrievePersonas();
  }, []);

  const retrievePersonas = () => {
    AgendaDataService.getAllPersonas()
      .then((response) => setPersonas(response.data))
      .catch((e) => console.log(e));
  };

  const handlePersonaClick = (persona) => {
    setSelectedPersona(persona);
    setModalState("informacion");
  };

  const showTutorials = (persona) => {
    setTutorials(persona.tutoriales || []);
    setModalState("tutoriales");
  };

  const selectTutorial = (tutorial) => {
    setSelectedTutorial(tutorial);
  };

  const closeModal = () => {
    setSelectedPersona(null);
    setSelectedTutorial(null);
  };

  const cerrarTutorialModal = () => {
    setModalState("");
    setSelectedTutorial(null);
  };

  return (
    <div className="fondo-container">
      <Container id="contenido" className="mt-5">
        <Row className="mb-3 align-items-center">
          <Col md={12}>
            <div className="d-flex">
              <Form.Control
                type="text"
                placeholder="Buscar por nombre"
                value={searchPersona}
                onChange={(e) => setSearchPersona(e.target.value)}
                className="modern-input"
              />
            </div>
          </Col>
        </Row>

        <Row className="justify-content-center">
          <Col xs={12} md={8} lg={6} className="text-center">
            <h2 className="contactos-title">Contactos</h2>
          </Col>
        </Row>

        <Row className="mt-2 tabla-container">
          <Table striped bordered hover responsive className="modern-table">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Apellido</th>
              </tr>
            </thead>
            <tbody>
              {personas.map((persona) => (
                <tr
                  key={persona.id}
                  onClick={() => handlePersonaClick(persona)}
                  style={{ cursor: "pointer" }}
                >
                  <td>{persona.nombre}</td>
                  <td>{persona.apellido}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Row>

        {/* Modal de Información de la Persona */}
        {modalState === "informacion" && selectedPersona && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.5, ease: "easeOut" }}
          >
            <Modal show={true} onHide={closeModal}>
              <Modal.Header closeButton>
                <Modal.Title>Detalles de {selectedPersona?.nombre} {selectedPersona?.apellido}</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <div><strong>Dirección:</strong> {selectedPersona?.direccion}</div>
                <div><strong>Código Postal:</strong> {selectedPersona?.codigoPostal}</div>
                <div><strong>Ciudad:</strong> {selectedPersona?.ciudad}</div>
                <div><strong>Fecha de Nacimiento:</strong> {selectedPersona?.fechaNacimiento}</div>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="link" onClick={() => showTutorials(selectedPersona)}>
                  Ver Tutoriales
                </Button>
              </Modal.Footer>
            </Modal>
          </motion.div>
        )}

        {/* Modal de Tutoriales */}
        {modalState === "tutoriales" && (
          <motion.div
            initial={{ opacity: 0, x: 300 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -300 }}
            transition={{ duration: 0.5, ease: "easeInOut" }}
          >
            <Modal show={true} onHide={cerrarTutorialModal}>
              <Modal.Header closeButton>
                <Modal.Title>Tutoriales</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                {tutorials.length > 0 ? (
                  <ul>
                    {tutorials.map((tutorial, index) => (
                      <li
                        key={index}
                        onClick={() => selectTutorial(tutorial)}
                        style={{ cursor: "pointer", marginBottom: "10px" }}
                      >
                        {tutorial.title} {/* Mostrar el título del tutorial */}
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p>No hay tutoriales disponibles.</p>
                )}
              </Modal.Body>
              <Modal.Footer>
                <Button variant="primary" onClick={cerrarTutorialModal}>
                  Volver
                </Button>
                <Button variant="danger" onClick={cerrarTutorialModal}>
                  Cerrar
                </Button>
              </Modal.Footer>
            </Modal>
          </motion.div>
        )}

        {/* Modal del tutorial seleccionado */}
        {selectedTutorial && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.5, ease: "easeOut" }}
          >
            <Modal show={true} onHide={cerrarTutorialModal}>
              <Modal.Header closeButton>
                <Modal.Title>{selectedTutorial.title}</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                {selectedTutorial.url && (
                  <img
                    src={selectedTutorial.url}
                    alt={selectedTutorial.title}
                    className="tutorial-image"
                    style={{ width: "100%", height: "auto" }}
                  />
                )}
                <p>{selectedTutorial.description}</p>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="danger" onClick={cerrarTutorialModal}>
                  Cerrar
                </Button>
              </Modal.Footer>
            </Modal>
          </motion.div>
        )}
      </Container>
    </div>
  );
};

export default Inicio;
