import React, { Component } from "react";
import { Container, Table, Button, Form } from "react-bootstrap";
import { motion, AnimatePresence } from "framer-motion";

export default class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tutorials: [],
      currentTutorial: null,
      currentIndex: -1,
      searchTitle: "",
    };
  }

  componentDidMount() {
    const { location } = this.props;
    // Verificar si hay tutoriales pasados desde la ruta
    if (location.state && location.state.tutoriales) {
      this.setState({ tutorials: location.state.tutoriales });
    } else {
      this.retrieveTutorials();
    }
  }

  onChangeSearchTitle = (e) => {
    this.setState({ searchTitle: e.target.value });
  };

  retrieveTutorials = () => {
    // Aquí puedes agregar lógica para recuperar todos los tutoriales
    // Si no quieres hacer la llamada a un servicio, puedes quitar este bloque.
  };

  searchTitle = () => {
    // Lógica para buscar tutoriales por título (si lo necesitas).
  };

  setActiveTutorial = (tutorial, index) => {
    this.setState({
      currentTutorial: this.state.currentIndex === index ? null : tutorial,
      currentIndex: this.state.currentIndex === index ? -1 : index,
    });
  };

  render() {
    const { searchTitle, tutorials, currentIndex } = this.state;

    return (
      <Container className="mt-5">
        <div className="mb-4 d-flex align-items-center search-container">
          <Form.Control
            type="text"
            placeholder="Buscar por título..."
            value={searchTitle}
            onChange={this.onChangeSearchTitle}
            className="search-input"
          />
          <Button variant="primary" className="ms-2 search-btn" onClick={this.searchTitle}>
            Buscar
          </Button>
        </div>

        {tutorials.length === 0 ? (
          <div className="text-center">
            <h5>No hay tutoriales disponibles.</h5>
          </div>
        ) : (
          <Table borderless className="modern-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Descripción</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {tutorials.map((tutorial, index) => (
                <React.Fragment key={tutorial.id}>
                  <tr
                    onClick={() => this.setActiveTutorial(tutorial, index)}
                    className="table-row"
                  >
                    <td>{tutorial.title}</td>
                    <td>{tutorial.description}</td>
                    <td>{tutorial.published ? "Publicado" : "Pendiente"}</td>
                  </tr>
                  <AnimatePresence>
                    {currentIndex === index && (
                      <motion.tr
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        transition={{ duration: 0.3 }}
                        className="detail-row"
                      >
                        <td colSpan="3">
                          <div className="detail-content">
                            <p><strong>Fecha:</strong> {tutorial.date}</p>
                            <p><strong>Autor:</strong> {tutorial.author}</p>
                          </div>
                        </td>
                      </motion.tr>
                    )}
                  </AnimatePresence>
                </React.Fragment>
              ))}
            </tbody>
          </Table>
        )}
      </Container>
    );
  }
}
