import React, { useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { Menu } from "./components/Menu";
import Inicio from "./components/Inicio";
import Añadir from "./components/Añadir";
import Perfil from "./components/Perfil";
import Tutoriales from "./components/Tutoriales";
import Editar from "./components/Editar";
import Login from "./components/Login";
import UserProvider from "./provider/UserProvider";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group"; // Importar transiciones

function App() {
  const [personas, setPersonas] = useState([]);

  return (
    <Router>
      <UserProvider>
        <Menu />
        <div className="container mt-3">
          <TransitionGroup>
            <Switch>
              <Route
                exact
                path="/"
                render={(props) => (
                  <CSSTransition timeout={500} classNames="fade" unmountOnExit>
                    <Inicio {...props} personas={personas} />
                  </CSSTransition>
                )}
              />
              <Route
                exact
                path="/añadir"
                render={(props) => (
                  <CSSTransition timeout={500} classNames="fade" unmountOnExit>
                    <Añadir {...props} />
                  </CSSTransition>
                )}
              />
              <Route
                exact
                path="/login"
                render={(props) => (
                  <CSSTransition timeout={500} classNames="fade" unmountOnExit>
                    <Login {...props} />
                  </CSSTransition>
                )}
              />
              <Route
                path="/perfil"
                render={(props) => (
                  <CSSTransition timeout={500} classNames="fade" unmountOnExit>
                    <Perfil {...props} />
                  </CSSTransition>
                )}
              />
              <Route
                path="/editar"
                render={(props) => (
                  <CSSTransition timeout={500} classNames="fade" unmountOnExit>
                    <Editar {...props} />
                  </CSSTransition>
                )}
              />
              <Route
                path="/tutoriales"
                render={(props) => (
                  <CSSTransition timeout={500} classNames="fade" unmountOnExit>
                    <Tutoriales {...props} />
                  </CSSTransition>
                )}
              />
            </Switch>
          </TransitionGroup>
        </div>
      </UserProvider>
    </Router>
  );
}

export default App;
