import React, { useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { Menu } from "./components/Menu";
import Inicio from "./components/Inicio";
import Añadir from "./components/Añadir";
import Perfil from "./components/Perfil";
import Tutoriales from "./components/Tutoriales";
import Editar from "./components/Editar";
import Login from "./components/Login";
import UserProvider from "./provider/UserProvider";
import { BrowserRouter as Router, Route, Switch, useLocation } from "react-router-dom"; // Importar useLocation correctamente
import { CSSTransition, TransitionGroup } from 'react-transition-group';

function App() {
  const [personas, setPersonas] = useState([]);

  const updatePersona = (updatedPersona) => {
    setPersonas((prevPersonas) =>
      prevPersonas.map((persona) =>
        persona.id === updatedPersona.id ? updatedPersona : persona
      )
    );
  };

  return (
    <Router> 
      <UserProvider>
        <Menu />
        <div className="container mt-3">
          <AppRoutes /> {/* Llamamos a un componente separado para manejar las rutas */}
        </div>
      </UserProvider>
    </Router>
  );
}

// Componente que maneja las rutas con transiciones
function AppRoutes() {
  const location = useLocation(); // useLocation dentro de un Router

  return (
    <TransitionGroup>
      <CSSTransition
        key={location.key}
        timeout={600}
        classNames="fade"
      >
        <Switch location={location}>
          <Route 
            exact 
            path="/" 
            render={(props) => <Inicio {...props} personas={personas} />} 
          />
          <Route exact path="/añadir" component={Añadir} />
          <Route exact path="/login" component={Login} />
          <Route path="/perfil" render={(props) => <Perfil {...props} />} />
          <Route path="/editar/:id" component={Editar} />
          <Route path="/tutoriales/" component={Tutoriales} />
        </Switch>
      </CSSTransition>
    </TransitionGroup>
  );
}

export default App;
