import React, { useEffect, useState } from 'react';
import AgendaDataService from '../services/agenda.service';
import TutorialDataService from '../services/tutorial.service';
import { useHistory, useLocation } from 'react-router-dom';
import { Modal, ListGroup, Form, Button, Container, Col, Row, Card } from 'react-bootstrap';
import '../styles/Añadir-Editar.css';
import { Alert } from 'react-bootstrap';

function EditPersona() {
  const id = window.location.pathname.split('/')[2];
  const history = useHistory();
  const location = useLocation();
  const updatePersonaInList = location.state?.updatePersonaInList;

  const [persona, setPersona] = useState({
    id: id,
    nombre: '',
    apellido: '',
    direccion: '',
    codigoPostal: '',
    ciudad: '',
    fechaNacimiento: '',
    tutoriales: [] // Guardaremos los ids de los tutoriales
  });

  const [availableTutorials, setAvailableTutorials] = useState([]);
  const [selectedTutorials, setSelectedTutorials] = useState([]); // Ahora almacena los ids
  const [showTutorialModal, setShowTutorialModal] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    TutorialDataService.getAllTutorials()
      .then(response => setAvailableTutorials(response.data))
      .catch(error => console.error('Error al cargar tutoriales:', error));

    AgendaDataService.getPersona(id)
      .then(response => {
        setPersona(response.data);
        // Al obtener la persona, cargamos los ids de los tutoriales
        setSelectedTutorials(response.data.tutoriales.map(tut => tut.id) || []);
      })
      .catch(error => console.error("Error al obtener la persona:", error));
  }, [id]);

  const editPersona = (e) => {
    e.preventDefault();

     // Verifica si algún campo requerido está vacío
     if (!persona.nombre || !persona.apellido || !persona.direccion || !persona.codigoPostal || !persona.ciudad || !persona.fechaNacimiento) {
      setErrorMessage("Error: Todos los campos son obligatorios.");
      return; // Detiene la ejecución si algún campo está vacío
    }

    // Guardamos solo los ids de los tutoriales seleccionados
    const personaData = { ...persona, tutoriales: selectedTutorials };
    AgendaDataService.updatePersona(id, personaData)
      .then(() => {
        if (updatePersonaInList) updatePersonaInList(personaData);
        history.push("/");
      })
      .catch(error => console.error("Error al actualizar la persona:", error));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPersona({
      ...persona,
      [name]: value,
    });
  };

  const toggleTutorialSelection = (tutorialId) => {
    if (selectedTutorials.includes(tutorialId)) {
      setSelectedTutorials(selectedTutorials.filter(id => id !== tutorialId)); // Eliminamos el tutorial si está seleccionado
    } else {
      setSelectedTutorials([...selectedTutorials, tutorialId]); // Agregamos el tutorial si no está seleccionado
    }
  };

  const openTutorialModal = () => setShowTutorialModal(true);
  const closeTutorialModal = () => setShowTutorialModal(false);

  return (
    <Container className="contenedor-formulario">
      <Card className="formulario-card">
        <h3 className="titulo-formulario">Editar Persona</h3>
        <Form onSubmit={editPersona}>
          {/* Formulario para editar persona */}
          <Row className="mb-4">
            <Form.Group as={Col} controlId="nombre">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Introduce nombre"
                name="nombre"
                value={persona.nombre}
                onChange={handleChange}
                className="form-control-custom"
              />
            </Form.Group>
            <Form.Group as={Col} controlId="apellido">
              <Form.Label>Apellido</Form.Label>
              <Form.Control
                type="text"
                placeholder="Introduce apellido"
                name="apellido"
                value={persona.apellido}
                onChange={handleChange}
                className="form-control-custom"
              />
            </Form.Group>
          </Row>

          {/* Otros campos del formulario */}
          {/* ... */}

          {/* Mostrar tutoriales seleccionados */}
          <Form.Group className="mb-4">
            <Form.Label>Tutoriales asignados</Form.Label>
            <div>
              {selectedTutorials.length > 0
                ? `Seleccionados: ${selectedTutorials.map(id => {
                    const tutorial = availableTutorials.find(tut => tut.id === id);
                    return tutorial ? tutorial.title : 'Desconocido';
                  }).join(", ")}`
                : "Ningún tutorial seleccionado"}
            </div>
            <Button className="button-custom mt-2" onClick={openTutorialModal}>
              Seleccionar Tutoriales
            </Button>
          </Form.Group>

          <Button
            variant="primary"
            type="submit"
            className="boton-enviar"
          >
            Actualizar
          </Button>

          {/* Mostrar el mensaje de error con Alert si hay un error */}
          {errorMessage && (
            <Alert variant="danger" className='mt-4'>
              <strong>{errorMessage}</strong>
            </Alert>
          )}
        </Form>
      </Card>

      {/* Modal para seleccionar tutoriales */}
      <Modal show={showTutorialModal} onHide={closeTutorialModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Seleccionar Tutoriales</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="tutorials-grid">
            {availableTutorials.map((tutorial) => (
              <div
                key={tutorial.id}
                className={`tutorial-card ${selectedTutorials.includes(tutorial.id) ? "selected" : ""}`}
                onClick={() => toggleTutorialSelection(tutorial.id)}
              >
                <img
                  src={tutorial.url || "https://via.placeholder.com/80"} // Imagen por defecto si no hay URL
                  alt={tutorial.title}
                  className="tutorial-img"
                />
                <div>
                  <h5 className="tutorial-title">{tutorial.title}</h5>
                  <p className="tutorial-description">{tutorial.description || "Sin descripción"}</p>
                </div>
              </div>
            ))}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button className="confirm-button" onClick={closeTutorialModal}>
            Confirmar selección
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default EditPersona;
