import React, { useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { Menu } from "./components/Menu"; // Importamos el componente del menú
import Inicio from "./components/Inicio"; // Importamos el componente Inicio
import Añadir from "./components/Añadir"; // Importamos el componente Añadir
import Perfil from "./components/Perfil";
import Tutoriales from "./components/Tutoriales";
import Editar from "./components/Editar";
import Login from "./components/Login"; // Importamos el componente Login
import UserProvider from "./provider/UserProvider"; // Correcta importación de UserProvider
import { BrowserRouter as Router, Route, Switch, use } from "react-router-dom"; // Importa Switch y Route de react-router-dom v5

// Importa los componentes de react-transition-group
import { CSSTransition, TransitionGroup } from 'react-transition-group';

function App() {
  // Aquí asumo que tienes un estado para personas en App.js (o en un lugar global)
  const [personas, setPersonas] = useState([]);
  const location = useNavigate();  // Usamos el hook para obtener la ubicación actual

  return (
    <Router> 
      <UserProvider> 
        <Menu /> {/* Este es el menú de navegación */}
        <div className="container mt-3">
          {/* Este TransitionGroup envuelve el contenido que cambia entre rutas */}
          <TransitionGroup>
            <CSSTransition
              key={location.key}  // Necesitamos la key para identificar el cambio de ruta
              timeout={500} // Duración de la transición
              classNames="fade" // Nombre de la clase para la animación
            >
              <div>
                <Switch location={location}>
                  <Route exact path="/" component={Inicio} />
                  <Route exact path="/añadir" component={Añadir} />
                  <Route exact path="/login" component={Login} />
                  <Route path="/perfil" component={Perfil} />
                  <Route path="/editar/" component={Editar} />
                  <Route path="/tutoriales" component={Tutoriales} />
                </Switch>
              </div>
            </CSSTransition>
          </TransitionGroup>
        </div>
      </UserProvider>
    </Router>
  );
}

export default App;
