import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Tutoriales.css"; 
import { Container, Table, Button, Form, Modal } from "react-bootstrap";
import { motion, AnimatePresence } from "framer-motion";

export default class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tutorials: [],
      currentTutorial: null,
      currentIndex: -1,
      searchTitle: "",
      showModal: false,   // Estado para mostrar el modal
      selectedTutorial: null // Estado para guardar el tutorial seleccionado
    };
  }

  componentDidMount() {
    const { location } = this.props;
    if (location.state && location.state.tutoriales) {
      this.setState({ tutorials: location.state.tutoriales });
    } else {
      this.retrieveTutorials();
    }
  }

  onChangeSearchTitle = (e) => {
    this.setState({ searchTitle: e.target.value });
  };

  retrieveTutorials = () => {
    TutorialDataService.getAllTutorials()
      .then(response => {
        this.setState({ tutorials: response.data });
      })
      .catch(e => console.log(e));
  };

  searchTitle = () => {
    TutorialDataService.findByTitle(this.state.searchTitle)
      .then(response => {
        this.setState({
          tutorials: response.data,
          currentTutorial: null,
          currentIndex: -1
        });
      })
      .catch(e => console.log(e));
  };

  setActiveTutorial = (tutorial, index) => {
    this.setState({
      currentTutorial: this.state.currentIndex === index ? null : tutorial,
      currentIndex: this.state.currentIndex === index ? -1 : index
    });
  };

  // Función para abrir el modal y seleccionar el tutorial
  openModal = (tutorial) => {
    this.setState({ showModal: true, selectedTutorial: tutorial });
  };

  // Función para cerrar el modal
  closeModal = () => {
    this.setState({ showModal: false, selectedTutorial: null });
  };

  render() {
    const { searchTitle, tutorials, currentIndex, showModal, selectedTutorial } = this.state;

    return (
      <Container className="mt-5">
        {/* Buscador */}
        <div className="mb-4 d-flex align-items-center search-container">
          <Form.Control
            type="text"
            placeholder="Buscar por título..."
            value={searchTitle}
            onChange={this.onChangeSearchTitle}
            className="search-input"
          />
          <Button variant="primary" className="ms-2 search-btn" onClick={this.searchTitle}>
            Buscar
          </Button>
        </div>

        {tutorials.length === 0 ? (
          <div className="text-center">
            <h5>No hay tutoriales disponibles.</h5>
          </div>
        ) : (
          <Table borderless className="modern-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {tutorials.map((tutorial, index) => (
                <React.Fragment key={tutorial.id}>
                  <tr
                    onClick={() => this.setActiveTutorial(tutorial, index)}
                    className="table-row"
                  >
                    <td>{tutorial.title}</td>
                    <td>
                      <Button 
                        variant="info" 
                        onClick={() => this.openModal(tutorial)}
                      >
                        Ver más
                      </Button>
                    </td>
                  </tr>
                </React.Fragment>
              ))}
            </tbody>
          </Table>
        )}

        {/* Modal para mostrar la imagen y el nombre de la persona */}
        <Modal
          show={showModal}
          onHide={this.closeModal}
          centered
          size="sm"
        >
          <Modal.Header closeButton>
            <Modal.Title>Detalles del Tutorial</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {selectedTutorial && (
              <div className="modal-content">
                <div className="image-container">
                  <img
                    src={selectedTutorial.url}
                    alt="Tutorial"
                    className="img-fluid"
                  />
                </div>
                <div className="instructor-info mt-3">
                  <p><strong>Impartido por:</strong> {selectedTutorial.instructorName}</p>
                </div>
              </div>
            )}
          </Modal.Body>
        </Modal>
      </Container>
    );
  }
}
