import React, { useEffect, useState } from 'react';
import AgendaDataService from '../services/agenda.service';
import TutorialDataService from '../services/tutorial.service';
import { useHistory, useLocation } from 'react-router-dom';
import { Modal, ListGroup, Form, Button, Container, Col, Row, Card } from 'react-bootstrap';
import '../styles/Editar.css'; // Importamos el CSS externo

function EditPersona() {
  const id = window.location.pathname.split('/')[2];
  const history = useHistory();
  const location = useLocation();
  const updatePersonaInList = location.state?.updatePersonaInList;

  const [persona, setPersona] = useState({
    id: id,
    nombre: '',
    apellido: '',
    direccion: '',
    codigoPostal: '',
    ciudad: '',
    fechaNacimiento: '',
    tutoriales: []
  });

  const [availableTutorials, setAvailableTutorials] = useState([]);
  const [selectedTutorials, setSelectedTutorials] = useState([]);
  const [showTutorialModal, setShowTutorialModal] = useState(false);

  useEffect(() => {
    TutorialDataService.getAllTutorials()
      .then(response => setAvailableTutorials(response.data))
      .catch(error => console.error('Error al cargar tutoriales:', error));

    AgendaDataService.getPersona(id)
      .then(response => {
        setPersona(response.data);
        setSelectedTutorials(response.data.tutoriales || []);
      })
      .catch(error => console.error("Error al obtener la persona:", error));
  }, [id]);

  const editPersona = (e) => {
    e.preventDefault();
    const personaData = { ...persona, tutoriales: selectedTutorials };
    AgendaDataService.updatePersona(id, personaData)
      .then(() => {
        if (updatePersonaInList) updatePersonaInList(personaData);
        history.push("/");
      })
      .catch(error => console.error("Error al actualizar la persona:", error));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPersona({
      ...persona,
      [name]: value,
    });
  };

  const toggleTutorialSelection = (tutorialId) => {
    const tutorial = availableTutorials.find(tut => tut.id === tutorialId);
    if (tutorial) {
      if (selectedTutorials.includes(tutorial.title)) {
        setSelectedTutorials(selectedTutorials.filter(title => title !== tutorial.title));
      } else {
        setSelectedTutorials([...selectedTutorials, tutorial.title]);
      }
    }
  };

  return (
    <Container className="contenedor-editar">
      <Card className="editar-card">
        <h3 className="titulo-editar">Editar Persona</h3>
        <Form onSubmit={editPersona}>
          <Row>
            <Form.Group as={Col} controlId="nombre">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Introduce nombre"
                name="nombre"
                value={persona.nombre}
                onChange={handleChange}
                className="form-control-custom"
              />
            </Form.Group>
            <Form.Group as={Col} controlId="apellido">
              <Form.Label>Apellido</Form.Label>
              <Form.Control
                type="text"
                placeholder="Introduce apellido"
                name="apellido"
                value={persona.apellido}
                onChange={handleChange}
                className="form-control-custom"
              />
            </Form.Group>
          </Row>

          <Form.Group controlId="direccion">
            <Form.Label>Dirección</Form.Label>
            <Form.Control
              type="text"
              placeholder="Introduce dirección"
              name="direccion"
              value={persona.direccion}
              onChange={handleChange}
              className="form-control-custom"
            />
          </Form.Group>

          <Row>
            <Form.Group as={Col} controlId="codigoPostal">
              <Form.Label>Código Postal</Form.Label>
              <Form.Control
                type="text"
                placeholder="Introduce código postal"
                name="codigoPostal"
                value={persona.codigoPostal}
                onChange={handleChange}
                className="form-control-custom"
              />
            </Form.Group>
            <Form.Group as={Col} controlId="ciudad">
              <Form.Label>Ciudad</Form.Label>
              <Form.Control
                type="text"
                placeholder="Introduce ciudad"
                name="ciudad"
                value={persona.ciudad}
                onChange={handleChange}
                className="form-control-custom"
              />
            </Form.Group>
          </Row>

          <Form.Group controlId="fechaNacimiento">
            <Form.Label>Fecha de Nacimiento</Form.Label>
            <Form.Control
              type="date"
              name="fechaNacimiento"
              value={persona.fechaNacimiento}
              onChange={handleChange}
              className="form-control-custom"
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Tutoriales asignados</Form.Label>
            <div>
              {selectedTutorials.length > 0
                ? `Seleccionados: ${selectedTutorials.join(", ")}`
                : "Ningún tutorial seleccionado"}
            </div>
            <Button className="boton-seleccionar" onClick={() => setShowTutorialModal(true)}>
              Seleccionar Tutoriales
            </Button>
          </Form.Group>

          <Button variant="primary" type="submit" className="boton-actualizar">
            Actualizar
          </Button>
        </Form>
      </Card>

      {/* Modal de selección de tutoriales */}
      <Modal show={showTutorialModal} onHide={() => setShowTutorialModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Seleccionar Tutoriales</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="tutorials-grid">
            {availableTutorials.map((tutorial) => (
              <div
                key={tutorial.id}
                className={`tutorial-card ${selectedTutorials.includes(tutorial.title) ? "selected" : ""}`}
                onClick={() => toggleTutorialSelection(tutorial.id)}
              >
                <img
                  src={tutorial.url || "https://via.placeholder.com/80"}
                  alt={tutorial.title}
                  className="tutorial-img"
                />
                <div className="tutorial-content">
                  <h5 className="tutorial-title">{tutorial.title}</h5>
                  <p className="tutorial-description">
                    {tutorial.description || "Sin descripción"}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button className="boton-confirmar" onClick={() => setShowTutorialModal(false)}>
            Confirmar selección
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default EditPersona;
