import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Tutoriales.css";
import { Container, Table, Button, Form, Modal } from "react-bootstrap";
import { motion, AnimatePresence } from "framer-motion";
import buscar1 from "../assets/buscar1.png";

export default class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tutorials: [], // Lista de tutoriales
      currentTutorial: null,
      currentIndex: -1,
      searchTitle: "",
      showModal: false, // Controla la visibilidad del modal
      selectedTutorial: null, // Guarda el tutorial seleccionado para mostrar en el modal
    };
  }

  componentDidMount() {
    const { location } = this.props;
    if (location.state && location.state.tutoriales) {
      this.setState({ tutorials: location.state.tutoriales });
    } else {
      this.retrieveTutorials();
    }
  }

  onChangeSearchTitle = (e) => {
    this.setState({ searchTitle: e.target.value });
  };

  retrieveTutorials = () => {
    TutorialDataService.getAllTutorials()
      .then(response => {
        this.setState({ tutorials: response.data });
      })
      .catch(e => console.log(e));
  };

  searchTitle = () => {
    TutorialDataService.findByTitle(this.state.searchTitle)
      .then(response => {
        this.setState({
          tutorials: response.data,
          currentTutorial: null,
          currentIndex: -1
        });
      })
      .catch(e => console.log(e));
  };

  setActiveTutorial = (tutorial, index) => {
    this.setState({
      currentTutorial: this.state.currentIndex === index ? null : tutorial,
      currentIndex: this.state.currentIndex === index ? -1 : index,
    });
  };

  showMoreInfo = (tutorial) => {
    this.setState({
      showModal: true,
      selectedTutorial: tutorial,
    });
  };

  handleCloseModal = () => {
    this.setState({
      showModal: false,
      selectedTutorial: null,
    });
  };

  render() {
    const { searchTitle, tutorials, currentTutorial, currentIndex, showModal, selectedTutorial } = this.state;

    return (
      <Container>
        <div className="search-container">
          <Form.Control
            type="text"
            value={searchTitle}
            onChange={this.onChangeSearchTitle}
            placeholder="Buscar tutorial"
            className="search-input"
          />
          <Button onClick={this.searchTitle} className="search-btn">
            <img src={buscar1} alt="Buscar" className="search-icon" />
          </Button>
        </div>

        <Table striped bordered hover responsive className="tutorial-table">
          <thead>
            <tr>
              <th>T√≠tulo</th>
              <th>Descripci√≥n</th>
            </tr>
          </thead>
          <tbody>
            {tutorials.map((tutorial, index) => (
              <tr
                key={tutorial.id}
                onClick={() => this.setActiveTutorial(tutorial, index)}
                style={{ cursor: "pointer" }}
              >
                <td>{tutorial.title}</td>
                <td>{tutorial.description}</td>
              </tr>
            ))}
          </tbody>
        </Table>

        {/* Modal con m√°s detalles del tutorial */}
        <AnimatePresence>
          {showModal && selectedTutorial && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              transition={{ duration: 0.5 }}
            >
              <Modal show={showModal} onHide={this.handleCloseModal} className="tutorial-modal">
                <Modal.Header closeButton>
                  <Modal.Title>{selectedTutorial.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <div className="tutorial-detail">
                    {selectedTutorial.url && (
                      <div className="tutorial-detail-item">
                        <img src={selectedTutorial.url} alt="Tutorial" className="tutorial-image" />
                      </div>
                    )}
                    <div className="tutorial-detail-item">
                      <strong>Descripci√≥n:</strong> {selectedTutorial.description || "Descripci√≥n no disponible"}
                    </div>
                  </div>
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={this.handleCloseModal}>
                    Cerrar
                  </Button>
                </Modal.Footer>
              </Modal>
            </motion.div>
          )}
        </AnimatePresence>
      </Container>
    );
  }
}
