import React, { useState } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import { Menu } from "./components/Menu"; 
import Inicio from "./components/Inicio";
import Añadir from "./components/Añadir";
import Perfil from "./components/Perfil";
import Tutoriales from "./components/Tutoriales";
import Editar from "./components/Editar";
import Login from "./components/Login";
import UserProvider from "./provider/UserProvider"; 
import { BrowserRouter as Router, Route, Switch, useLocation } from "react-router-dom"; 
import { CSSTransition, TransitionGroup } from 'react-transition-group'; // Importa CSSTransition y TransitionGroup

function App() {
  const [personas, setPersonas] = useState([]);

  const updatePersona = (updatedPersona) => {
    setPersonas((prevPersonas) =>
      prevPersonas.map((persona) =>
        persona.id === updatedPersona.id ? updatedPersona : persona
      )
    );
  };

  const location = useLocation(); // Obtiene la ubicación actual de la URL

  return (
    <Router> 
      <UserProvider> 
        <Menu />
        <div className="container mt-3">
          <TransitionGroup> {/* Ensamblar los cambios de rutas dentro del grupo de transiciones */}
            <CSSTransition 
              key={location.key} // Asegura que la clave de la transición sea única para cada cambio de ruta
              timeout={600} // Duración de la transición
              classNames="fade" // Utiliza las clases de CSS para las transiciones
            >
              <Switch location={location}> {/* Pasar location de react-router para asegurar que las transiciones ocurran correctamente */}
                <Route 
                  exact 
                  path="/" 
                  render={(props) => <Inicio {...props} personas={personas} />} 
                />
                <Route exact path="/añadir" component={Añadir} />
                <Route exact path="/login" component={Login} />
                <Route path="/perfil" render={(props) => <Perfil {...props} />} />
                <Route path="/editar/:id" component={Editar} />
                <Route path="/tutoriales/" component={Tutoriales} />
              </Switch>
            </CSSTransition>
          </TransitionGroup>
        </div>
      </UserProvider>
    </Router>
  );
}

export default App;
