import React, { Component } from "react";
import { Container, Table } from "react-bootstrap";
import { Link } from "react-router-dom";

class Tutoriales extends Component {
  state = {
    searchTitle: "",
    tutorials: [], // Aquí se guardarán los tutoriales
    currentIndex: -1,
    showModal: false,
    selectedTutorial: null,
  };

  // Al montar el componente, buscamos los tutoriales
  componentDidMount() {
    const { location } = this.props;
    if (location.state && location.state.tutoriales) {
      console.log("Tutoriales recibidos:", location.state.tutoriales);
      this.setState({ tutorials: location.state.tutoriales });
    } else {
      this.retrieveTutorials();
    }
  }

  // Simulamos la obtención de los tutoriales si no vienen desde location.state
  retrieveTutorials() {
    // Aquí podrías hacer una llamada a la API o tener datos estáticos
    const sampleTutorials = [
      { title: "Introducción a Spring Boot" },
      { title: "La Cuarta" },
    ];
    this.setState({ tutorials: sampleTutorials });
  }

  render() {
    const { searchTitle, tutorials, currentIndex, showModal, selectedTutorial } = this.state;

    // Verificamos el estado de los tutoriales
    console.log("Tutoriales:", tutorials);

    return (
      <Container className="mt-5">
        {tutorials.length === 0 ? (
          <div className="text-center">
            <h5>No hay tutoriales disponibles.</h5>
          </div>
        ) : (
          <Table borderless className="modern-table">
            <thead>
              <tr>
                <th>Título</th>
              </tr>
            </thead>
            <tbody>
              {tutorials.map((tutorial, index) => (
                <tr key={index}>
                  <td>{tutorial.title}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}

        {/* Aquí puedes incluir el enlace a otra página, si lo necesitas */}
        <Link
          to={{
            pathname: "/tutoriales",
            state: { tutoriales: tutorials },
          }}
        >
          Ver tutoriales
        </Link>
      </Container>
    );
  }
}

export default Tutoriales;
