import React, { useContext, useState } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { UserContext } from "../provider/UserProvider";
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Importamos motion
import icono from "../assets/usuario.png";
import "../styles/Menu.css"; // Asegúrate de importar el archivo CSS

export function Menu() {
  const userContext = useContext(UserContext);
  const [currentPersona, setCurrentPersona] = useState(null);

  const nombre = userContext?.displayName || "Invitado"; 
  const foto = userContext?.photoURL || icono;

  // Animaciones para los enlaces
  const linkVariants = {
    hidden: { opacity: 0, y: -10 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <Navbar bg="transparent" expand="lg" className="navbar-custom">
      <Container fluid>
        <Navbar.Brand href="#home" className="navbar-brand">
          <Link to="/perfil">
            <button className="user-button">
              {/* Nombre del usuario */}
              <h3 className="user-name">
                {nombre}
              </h3>
              {/* Imagen del usuario */}
              <img
                src={foto}
                alt="User Icon"
                className="user-icon"
              />
            </button>
          </Link>
        </Navbar.Brand>

        <Navbar.Toggle aria-controls="basic-navbar-nav" className="navbar-toggle" />

        <Navbar.Collapse id="basic-navbar-nav">
          <motion.Nav
            className="ml-auto"
            initial="hidden"
            animate="visible"
            variants={{
              hidden: { opacity: 0 },
              visible: { opacity: 1, transition: { duration: 0.3, ease: "easeOut" } },
            }}
          >
            <motion.div variants={linkVariants} transition={{ delay: 0.2 }}>
              <Nav.Link href="/" className="nav-item-custom">Inicio</Nav.Link>
            </motion.div>
            {userContext && (
              <motion.div variants={linkVariants} transition={{ delay: 0.3 }}>
                <Nav.Link href="/añadir" className="nav-item-custom">Añadir</Nav.Link>
              </motion.div>
            )}
            <motion.div variants={linkVariants} transition={{ delay: 0.4 }}>
              <Nav.Link href="/tutoriales" className="nav-item-custom">Tutoriales</Nav.Link>
            </motion.div>
            {!userContext ? (
              <motion.div variants={linkVariants} transition={{ delay: 0.5 }}>
                <Nav.Link href="/login" className="nav-item-custom">Login</Nav.Link>
              </motion.div>
            ) : null}
          </motion.Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Menu;
